- task: PowerShell@2 inputs: targetType: 'inline' script: | $uri = "$(System.CollectionUri)/$(System.TeamProject)/_apis/git/repositories?api-version=6.0" $body = @{ name = 'my-new-repo' project = @{ id = "$(System.TeamProjectId)" } } | ConvertTo-Json $headers = @{ Authorization = "Bearer $(System.AccessToken)" 'Content-Type' = 'application/json' } Invoke-RestMethod -Uri $uri -Method Post -Headers $headers -Body $body
